generator client {
  provider = "prisma-client-js"
  output   = "./generated"
}

datasource db {
  provider = "sqlite" // can be Postgres too
  url      = "file:./auth.db" // e.g., file:./auth.db
}

model User {
  id               String   @id @default(cuid())
  teacherProfileId String?
  email            String   @unique
  password         String
  firstName        String?
  lastName         String?
  createdAt        DateTime @default(now())
  updatedAt        DateTime @updatedAt
}

generator client {
  provider = "prisma-client-js"
  output   = "./generated"
}

datasource db {
  provider = "sqlite" // can be Postgres too
  url      = "file:./auth.db" // e.g., file:./auth.db
}

model User {
  id        String   @id @default(cuid())
  teacherProfileId       String?
  email     String   @unique
  password  String
  firstName String?
  lastName  String?
  createdAt DateTime @default(now())
  updatedAt DateTime @updatedAt

  @@index([teacherProfileId])

  studentId String?
}

generator client {
  provider = "prisma-client-js"
  output   = "./generated/client"
}

datasource db {
  provider = "sqlite"
  url      = "file:./teach.db"
}

model TeacherProfile {
  id          String   @id @default(cuid())
  bio         String
  subjects    String
  avatarUrl   String?
  verified    Boolean  @default(false)
  payoutEmail String?
  createdAt   DateTime @default(now())
}

